<script>
    var CSRFToken = $('meta[name="csrf-token"]').attr('content');
    var options = {
      filebrowserImageBrowseUrl: '/laravel-filemanager?type=Images',
      filebrowserImageUploadUrl: '/laravel-filemanager/upload?type=Images&_token='+CSRFToken,
      filebrowserBrowseUrl: '/laravel-filemanager?type=Files',
      filebrowserUploadUrl: '/laravel-filemanager/upload?type=Files&_token='+CSRFToken,
    };
  </script>

<script>


    

    setTimeout(function(){
        CKEDITOR.replace('historia', options);
    
            CKEDITOR.instances.historia.on('instanceReady', function() { 
                document.getElementById("historiatexto").value = CKEDITOR.instances.historia.getData();
            });

            CKEDITOR.instances.historia.on('change', function() { 
                document.getElementById("historiatexto").value = this.getData();
            });
    },100);

    
</script>
